list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX foaf: <http://xmlns.com/foaf/0.1/> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX vcard: <http://www.w3.org/2006/vcard/ns#> PREFIX fn: <http://www.w3.org/2005/xpath-functions#>
SELECT DISTINCT ?subclass ?editorship ?editor ?editorName WHERE { ?subject ?property ?editorship . ?editorship a core:Editorship . OPTIONAL { ?subject ?property ?editorship . ?editorship a core:Editorship . ?editorship core:rank ?rank . } OPTIONAL { ?subject ?property ?editorship . ?editorship a core:Editorship . ?editorship core:relates ?editor . ?editor a foaf:Agent . ?editor rdfs:label ?editorName . OPTIONAL { ?subject ?property ?editorship . ?editorship a core:Editorship . ?editorship core:relates ?editor . ?editor a foaf:Agent . ?editor vitro:mostSpecificType ?subclass . ?subclass rdfs:subClassOf foaf:Agent . } } OPTIONAL { ?subject ?property ?editorship . ?editorship a core:Editorship . ?editorship core:relates ?editor . ?editor a vcard:Kind . ?editor vcard:hasName ?vName . ?vName vcard:familyName ?lastName . OPTIONAL { ?subject ?property ?editorship . ?editorship a core:Editorship . ?editorship core:relates ?editor . ?editor a vcard:Kind . ?editor vcard:hasName ?vName . ?vName vcard:givenName ?firstName . } OPTIONAL { ?subject ?property ?editorship . ?editorship a core:Editorship . ?editorship core:relates ?editor . ?editor a vcard:Kind . ?editor vcard:hasName ?vName . ?vName core:middleName ?middleName . } bind ( COALESCE(?firstName, "") As ?firstName1) . bind ( COALESCE(?middleName, "") As ?middleName1) . bind ( COALESCE(?lastName, "") As ?lastName1) . bind (concat(str(str(?lastName1) + ", "),str(str(?firstName1) + " "),str(?middleName1)) as ?editorName) .
OPTIONAL { ?subject ?property ?editorship . ?editorship a core:Editorship . ?editorship core:relates ?editor . ?editor a vcard:Kind . ?editor vitro:mostSpecificType ?subclass . ?subclass rdfs:subClassOf vcard:Kind . } }
FILTER ( bound(?editor) )
} ORDER BY ?subclass ?rank (fn:lower-case(?editorName))
